mapper.select:
	select 
  
  from questions
  where subject=#{subject}
  limit #{offset},#{pageSize}

	select 
  
  from questions
  where subject=#{subject}
  
  and CHAR_LENGTH(answer)<=1
  
  order by rand()
  limit #{pageSize}

	select 
	
  from questions
  where chapter=#{chapter}
  limit #{offset},#{pageSize}
